import{_ as i,o as t,c as a,al as l}from"./chunks/framework.BoIi8QPn.js";const c=JSON.parse('{"title":"面试题","description":"","frontmatter":{"title":"面试题","date":"2026-03-01 21:24:00","categories":["web"],"tags":["基础"],"publish":false},"headers":[],"relativePath":"blogs/2026/interview.md","filePath":"blogs/2026/interview.md","lastUpdated":1772373459000}'),r={name:"blogs/2026/interview.md"};function o(s,e,n,p,_,d){return t(),a("div",null,[...e[0]||(e[0]=[l('<h2 id="有多个ip地址-如何最快的找到rtt最短的ip地址-假设最大并发数为10" tabindex="-1">有多个IP地址，如何最快的找到RTT最短的IP地址？假设最大并发数为10 <a class="header-anchor" href="#有多个ip地址-如何最快的找到rtt最短的ip地址-假设最大并发数为10" aria-label="Permalink to &quot;有多个IP地址，如何最快的找到RTT最短的IP地址？假设最大并发数为10&quot;">​</a></h2><p>思路：</p><ol><li>将IP地址分组，每组最大并发数为10。</li><li>对每组IP地址进行并发测试，记录IP地址的RTT。</li><li>先请求第一组。如果有一个IP地址的RTT明显短于其他IP地址，可以提前结束当前组的请求，直接返回该IP地址，并记录最短时间。</li><li>再请求下一组IP地址，如果当前组的RTT明显短于之前记录的最短时间，可以提前结束当前组的请求，直接返回该IP地址，并更新最短时间。</li><li>重复步骤4，直到所有组的IP地址都被测试完毕，最终返回RTT最短的IP地址。</li></ol><h2 id="实现红绿灯效果" tabindex="-1">实现红绿灯效果 <a class="header-anchor" href="#实现红绿灯效果" aria-label="Permalink to &quot;实现红绿灯效果&quot;">​</a></h2><p>思路：</p><ol><li>使用HTML和CSS创建一个红绿灯的结构，包括三个圆形元素分别代表红灯、黄灯和绿灯。</li><li>使用JavaScript来控制红绿灯的状态变化。可以使用setInterval函数来定时切换灯的状态。</li><li>定义一个状态变量来跟踪当前的灯状态，例如0代表红灯，1代表黄灯，2代表绿灯。</li><li>在定时器回调函数中，根据当前的状态变量来切换灯的颜色，并更新状态变量以准备下一次切换。 思路优化：</li><li>不使用定时器，因为定时器会在页面不可见时继续执行，浪费资源。可以使用requestAnimationFrame来实现更高效的动画效果。</li></ol>',6)])])}const T=i(r,[["render",o]]);export{c as __pageData,T as default};
